import { Injectable } from '@angular/core';
import { Volunteer } from '../volunteer/volunteer.model';
import { VolunteerService } from '../volunteer/volunteer.service';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SchedulingService {

  volunteerList: Volunteer[] = [];

  flag: boolean = false;

  constructor(private http: HttpClient, private volunteerService: VolunteerService) {
  }

  async getVoluteersByDay(day: number): Promise<Volunteer[]> {
    await this.volunteerService.getVolunteers().subscribe(data => {
      this.volunteerList = data; this.flag = true;
    }, err => { console.log(err); });
    return this.volunteerList.filter(v => v?.days[day] == true);
  }

  getScheduling(): Observable<Int32Array[]> {
    return this.http.get<Int32Array[]>("api/Scheduling/Get");
  }

  // putScheduling(id:number, volunteerId:number): Observable<Int16Array[]>{
  //   return this.http.put<Int32Array[]>("api/Scheduling/Put", {id, volunteerId});
  // }

}
